/* f_anomalia(id_utilizador, id_tempo, id_local, id_lingua, tipo_nomalia, com_proposta)
- id_utilizador: FK(d_ulizador)
- id_tempo: FK(d_tempo)
- id_local: FK (d_local)
- id_lingua: FK(d_lingua)*/


INSERT INTO f_anomalia(id_utilizador, id_tempo, id_local, id_lingua, tipo_anomalia, com_proposta)
SELECT u_id, t_id, loc_id, ling_id, tipo, com_prop
FROM(
    SELECT DISTINCT
        1 + trunc(random() * 200)::int AS u_id,
        1 + trunc(random() * 4018)::int AS t_id,
        1 + trunc(random() * 300)::int AS loc_id,
        1 + trunc(random() * 30)::int AS ling_id,
        trunc(random() * 2)::int AS tipo,
        trunc(random() * 2)::int AS com_prop
    FROM   generate_series(1, 1000) g
    LIMIT  1000
) random;
